mapper.select:
SELECT category_id , count(id) AS count
FROM questions
GROUP BY category_id;

SELECT q.id as q_id, q.title, q.type, q.multi, q.category_id, q.difficulty, q.score, q.analysis, q.create_time, q.update_time,
       qa.id as qa_id,qa.question_id as qa_question_id,qa.answer,qa.keywords
FROM questions q
         LEFT JOIN question_answers qa on q.id = qa.question_id and qa.is_deleted = 0

    q.is_deleted = 0

        and q.category_id = #{questionQueryVo.categoryId}


        and q.difficulty = #{questionQueryVo.difficulty}


        and q.type = #{questionQueryVo.type}


        and UPPER(q.title) LIKE UPPER(CONCAT('%', #{questionQueryVo.keyword}, '%'))


ORDER BY q.create_time desc
